# Function to plot confusion matrix
def plot_confusion_matrix(cm, classes, title='Confusion Matrix', cmap=plt.cm.Blues):
    plt.figure(figsize=(10, 7))
    sns.heatmap(cm, annot=True, fmt="d", linewidths=.5, cmap=cmap)
    plt.title(title)
    plt.ylabel('True label')
    plt.xlabel('Predicted label')
    tick_marks = np.arange(len(classes))
    plt.xticks(tick_marks + 0.5, classes, rotation=0)
    plt.yticks(tick_marks + 0.5, classes, rotation=0)
    plt.show()

# Function to plot metrics (precision, recall, f1-score)
def plot_metrics(report, metric_name, title):
    # Exclude non-class keys from the plotting data
    classes = [cls for cls in report.keys() if cls not in ['accuracy', 'macro avg', 'weighted avg']]
    values = [report[cls][metric_name] for cls in classes]

    plt.figure(figsize=(8, 4))
    sns.barplot(x=classes, y=values, palette='muted')
    plt.title(f'{title} for Each Class')
    plt.xlabel('Classes')
    plt.ylabel(metric_name.capitalize())
    plt.show()

# Define custom class labels
custom_classes = ['A', 'B', 'Γ']

# Plot confusion matrices
plot_confusion_matrix(test_conf_matrix, classes=custom_classes, title='Test Confusion Matrix')
plot_confusion_matrix(train_conf_matrix, classes=custom_classes, title='Training Confusion Matrix')

# Convert classification reports to dictionary
test_report_dict = classification_report(y_test, y_test_pred, output_dict=True)
train_report_dict = classification_report(y_train, y_train_pred, output_dict=True)

# Adjust keys in report dictionary to match custom class labels
test_report_dict = {custom_classes[int(k)]: v for k, v in test_report_dict.items() if k.isdigit()}
train_report_dict = {custom_classes[int(k)]: v for k, v in train_report_dict.items() if k.isdigit()}

# Plot precision, recall, f1-score for test data
plot_metrics(test_report_dict, 'precision', 'Test Precision')
plot_metrics(test_report_dict, 'recall', 'Test Recall')
plot_metrics(test_report_dict, 'f1-score', 'Test F1-Score')

# Plot precision, recall, f1-score for training data
plot_metrics(train_report_dict, 'precision', 'Training Precision')
plot_metrics(train_report_dict, 'recall', 'Training Recall')
plot_metrics(train_report_dict, 'f1-score', 'Training F1-Score')

# Plot overall accuracy
plt.figure(figsize=(8, 4))
accuracies = [test_accuracy, train_accuracy]
labels = ['Test Accuracy', 'Training Accuracy']
sns.barplot(x=labels, y=accuracies, palette='pastel')
plt.title('Overall Accuracy Comparison')
plt.ylabel('Accuracy')
plt.show()

def predict_language_levels(texts):
    # Create DataFrame from texts
    feature_list = [preprocess_and_extract_features(text) for text in texts]
    new_df = pd.DataFrame(feature_list)

    # Extract features using the previously fitted vectorizers and models
    new_tfidf_features = tfidf_vectorizer.transform(new_df['processed_text'])
    new_ngram_features = ngram_vectorizer.transform(new_df['processed_text'])
    new_word2vec_features = np.array([np.mean([w2v_model.wv[token] for token in tokens if token in w2v_model.wv] or [np.zeros(100)], axis=0) for tokens in new_df['tokens']])

    # Combine all features
    new_X = np.hstack((new_tfidf_features.toarray(), new_ngram_features.toarray(), new_word2vec_features, new_df[['MTLD', 'TTR', 'Lexical_Density', 'Syntactic_Density', 'avg_sentence_length', 'word_count', 'unique_word_count', 'text_length']].values))
    new_X_scaled = scaler.transform(new_X)

    # Predict language levels
    predicted_levels = best_model.predict(new_X_scaled)
    return label_encoder.inverse_transform(predicted_levels)

